$pshost = get-host
$pswindow = $pshost.ui.rawui
$newsize = $pswindow.buffersize
$newsize.height = 3000
$newsize.width = 3000
$pswindow.buffersize = $newsize

Try {
Function Get-UserCreds() {
    #Setup Variables
    $UserName       = "${workflow.scratchpad.scriptData.userName}"
    $VaultName = "${workflow.scratchpad.scriptData.vaultName}"
    $UserSecretName = "${workflow.scratchpad.scriptData.userSecretName}"

    $TenantId = "72f988bf-86f1-41af-91ab-2d7cd011db47"
    $Subscription = "ITSM Integration - Production - New"
    $VaultAuthUri = "http://169.254.169.254/metadata/identity/oauth2/token?api-version=2018-02-01&resource=https%3A%2F%2Fvault.azure.net"
    $KeyVaultUri = "https://$($VaultName).vault.azure.net"
    
    Try {
        # Authenticate with Machine Auth (Azure Managed Identity)
        $KeyVaultAuthToken = Invoke-RestMethod -Uri $VaultAuthUri -Method GET -Headers @{Metadata="true"} 
        $SecretValue = Invoke-RestMethod -Uri "$($KeyVaultUri)/secrets/$($UserSecretName)?api-version=2016-10-01" -Method GET -Headers @{Authorization="Bearer $($KeyVaultAuthToken.access_token)"}
        $UserSecret = ConvertTo-SecureString "$($SecretValue.value)" -AsPlainText -Force

        # Get the Secret/Creds
        $UserCreds = New-Object System.Management.Automation.PSCredential ($UserName, $UserSecret) -ErrorAction Stop       
        return $UserCreds
    } Catch {           
        Throw "Unable to authenticate with KeyVault to retrieve user credentials: $($_.Exception.ToString())"
    } Finally {
        # Null out secret
        $UserSecret = $null
        $SecretValue = $null
        $KeyVaultAuthToken = $null
        $VaultName = $null
    }
}


# Get Passed in Variables
$_sxArgs = @{}
$decodedArgs = [System.Text.Encoding]::UTF8.GetString([System.Convert]::FromBase64String("${workflow.scratchpad.scriptData.encodedVariableJson}"))
(ConvertFrom-Json "$decodedArgs").psobject.properties | Foreach { $_sxArgs[$_.Name] = $_.Value }

#Force PSSession to remote machine
$cred = Get-UserCreds
$global:_cmdOut = Invoke-Command -ScriptBlock {
    param([hashtable]$_sxArgs)
    $_sxOut = &{
        ${workflow.scratchpad.scriptData.script}
    } ${workflow.scratchpad.scriptData.inlineParameters}
    Write-Output $_sxOut
} -ArgumentList $_sxArgs

Write-Output $_cmdOut
} Catch {
    Write-Output $_.Exception.ToString()
} Finally {

}

var strOut = "Completed Executing Powershell Script " + workflow.scratchpad.scriptData.scriptName + ":\n\n";
strOut+=activity.output;
workflow.scratchpad.powershell_output = activity.output;
current.work_notes = strOut;
gs.addInfoMessage(strOut);
if(workflow.scratchpad.scriptData.incident_number!="") {
	var inc = new GlideRecord("incident");
	if(inc.get(workflow.scratchpad.scriptData.incident_number)) {
		inc.work_notes = strOut;
		inc.update();
	}
}

