Function Get-UserCreds() {
    #Setup Variables
    $CertSubjectName = "${workflow.scratchpad.scriptData.certSubjectName}"
    $AppId = "${workflow.scratchpad.scriptData.appId}"
    $VaultName = "${workflow.scratchpad.scriptData.vaultName}"

    $TenantId = "72f988bf-86f1-41af-91ab-2d7cd011db47"
    $Subscription = "ITSM Integration - Production - New"

    # Get the Cert
    $PrevLocation = Get-Location
    Try {
        Set-Location "cert:\LocalMachine\My" > $null    
        $AzCert = Get-ChildItem | where { $_.subject -eq "CN=$CertSubjectName" -and $_.NotAfter -gt [DateTime]::Now}
        if($AzCert -eq $null) {
            Throw "Certificate with subject CN=$CertSubjectName not found on host $($env:computername)."
        }
        if($AzCert.Length -gt 1) {
            $AzCert = $cert[0]
        }     

          Try {
            $ver = Get-InstalledModule "Az.Accounts" -AllVersions -ErrorAction SilentlyContinue | select Name,Version 
    } Catch {
        Try { 
            $ver = Get-InstalledModule "AzureRM.Profile" -AllVersions -ErrorAction SilentlyContinue | select Name,Version 
        } Catch { }
    }
        try {
             $UserSecret = $null    
            if($ver.Name -match "^Az\.") {
                $null = Connect-AzAccount -ApplicationId $AppId -CertificateThumbprint $AzCert.Thumbprint -Tenant $TenantId -ServicePrincipal
                 $UserSecret = Get-AzKeyVaultSecret -VaultName $VaultName -Name $UserSecretName -ErrorAction Stop        
                $null = Disconnect-AzAccount -ApplicationId $AppId -TenantId $TenantId
            }
            elseif($ver.Version.Major -le 4) {
                #AzureRM 4.4.1, AzureRM.Profile 3.4.1
                $null = Login-AzureRmAccount -ApplicationId $AppId -CertificateThumbprint $AzCert.Thumbprint -TenantId $TenantId -Subscription $Subscription -ServicePrincipal -ErrorAction Stop
                $UserSecret = Get-AzureKeyVaultSecret -VaultName $VaultName -Name $UserSecretName -ErrorAction Stop        
                $null = Remove-AzureRmAccount -ApplicationId $AppId -TenantId $TenantId
            } else {
                #AzureRM 5.7, AzureRM.Profile 4.6
                $null = Connect-AzureRmAccount -ApplicationId $AppId -CertificateThumbprint $AzCert.Thumbprint -TenantId $TenantId -Subscription $Subscription -ServicePrincipal -ErrorAction Stop
                $UserSecret = Get-AzureKeyVaultSecret -VaultName $VaultName -Name $UserSecretName -ErrorAction Stop        
                $null = Disconnect-AzureRmAccount -ApplicationId $AppId -TenantId $TenantId
            }
        } catch {
            Write-Output "AzureRM not found. Installing..."
            Install-Module -Name AzureRM -Force -AllowClobber 
            throw "Could not log in to Azure to retrieve user account info on host $($env:computername) ($($ver.Name) $($ver.Version)): $($_.Exception.ToString())"           
        }



        # Get the Secret/Creds
        $UserCreds = New-Object System.Management.Automation.PSCredential ($UserName, $UserSecret.SecretValue) -ErrorAction Stop
        
        # Null out secret
        $UserSecret = $null

        return $UserCreds
    } Catch {
        Throw
    } Finally {
        Set-Location $PrevLocation
    }
}

