var helpers = new x_micr5_scriptexe.ScriptExecutionHelpers();
var data = {
	server: workflow.variables.remote_host.getDisplayValue().toString(),
	scriptName: workflow.variables.script.name.toString(),
	script: workflow.variables.script.script.toString(),
	pssession_mode: workflow.variables.script.pssession_mode.toString(),
	approvalGroup: workflow.variables.script.approval_group,	
	certSubjectName: helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.CertificateSubjectName'),
	appId: helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.ScriptExecutionAppId'),
	vaultName: helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.KeyVaultName'),
	userName: workflow.variables.script.credential_username.toString(),
	userSecretName: workflow.variables.script.keyvault_credential_name.toString(),
	encodedVariableJson: "", // this will be a base64 encoded json string
	inlineParameters: "",
	approvalRequired: false,
	incident_number: workflow.variables.incident_number
};

var varJsonObj = {};

// Handle newlines in script (convert from \n to `n)
//data.script = data.script.replace(/[\r\n]+/gi,"\");

// script params are converted from json to a hashtable called $_sxArgs
// Inline params are given as -Name $_sxArgs."Name"
var scriptJson = global.JSON.parse(workflow.variables.script_parameters.toString());

var sxArgs = "$_sxArgs";
for(var i=0;i<scriptJson.length;i++) {	
	var param = scriptJson[i];
	data.inlineParameters += "-" + param.name + " " + sxArgs+".\"" + param.name + "\" ";
	if(param.value==null || param.value=="") {
		param.value = param.default_value;
	}
	
	// bool needs true/false, but gets empty string on false
	if((param.type=='bool') || (param.fieldtype=='True/False') || (param.htmlFieldType=='checkbox')) {
		varJsonObj[param.name] = (param.value.toString().toLowerCase()=='true') ? true : false;		
	} else {
		varJsonObj[param.name] = param.value;
	}
}

// base64 encode variables (since normal json strings have the wrong escape for PS)
var varJson = global.JSON.stringify(varJsonObj);
// gs.addInfoMessage("Json Vars:" + varJson);
data.encodedVariableJson = gs.base64Encode(varJson);
// gs.addInfoMessage("encoded json: " + data.encodedVariableJson);

// Add to work notes
var work_notes = "Executing script " + data.scriptName;
if(data.pssession_mode=='remote_server') {
	work_notes+=" on remote server: " + data.server;
}

// Check if approvals required
if(typeof(data.approvalGroup) == 'object' && data.approvalGroup!=null) {
	var approvalGroupName = data.approvalGroup.name.toString();
	if(approvalGroupName!="" && approvalGroupName!=null) {
		data.approvalRequired = true;
		work_notes += '. Executing this script requires approval from ' + approvalGroupName + '. Please wait for approval.';	
	}
}

current.work_notes = work_notes ;
workflow.scratchpad.scriptData = data;

