sys_variable_value.value:
function init() {	
	var data = {
		script: null,
		cred_type: workflow.variables.cred_type.toString(),	
		update_creds: workflow.variables.update_creds.toString()==='true',	
		cred_username: workflow.variables.cred_username.toString(),
		cred_password: workflow.variables.cred_password.toString(),
		user_groups:  workflow.variables.user_groups.toString(),
		owner_group: workflow.variables.owner_group.toString(),
		approval_group: workflow.variables.approval_group.toString()
	};

	data.script = new GlideRecord('x_micr5_scriptexe_script');
	if(!data.script.get(workflow.variables.script.sys_id.toString())) {
		current.comments = "Error retrieving script record. The script was not found/accessible.";
		return false;
	}
	
	
	
	if(data.script.user_groups != data.user_groups) {
		data.script.user_groups = data.user_groups;	
		current.comments = "Updated script user groups";
	}

	// Update Script Table
	if(data.script.owner_group != data.owner_group) {
		data.script.owner_group = data.owner_group;
		current.comments = "Updated script owner group.";
	}
	
	if(data.script.approval_group != data.approval_group) {
		data.script.approval_group = data.approval_group;
		current.comments = "Updated script approval group.";
	}
	
	if(data.update_creds==true) {						
		if(data.script.credential_username != data.cred_username) {
			data.script.credential_username = data.cred_username;
			current.comments = "Updated Credential Username to " + data.cred_username;
		}
		
		data.script.credential_type = data.cred_type;							
		data.keyvault_credential_name = getKeyVaultCredentialName(data.cred_username);		
		data.script.keyvault_credential_name = data.keyvault_credential_name;
	}
	
	data.script.update();
	workflow.scratchpad.scriptData = data;	
	
	// update ritm desc
	current.short_description = "Update script " + data.script.name.toString();
}

function getKeyVaultCredentialName(accountName) {
	var slashIndex = accountName.indexOf('\\');
	var atIndex = accountName.indexOf('@');  

	accountName = accountName.trim();  
	if(slashIndex >=0) {
		accountName = accountName.substring(slashIndex+1);	
		return getKeyVaultCredentialName(accountName);
	}
	if(atIndex>=0) {
		accountName = accountName.substring(0, atIndex);
		return getKeyVaultCredentialName(accountName);
	}

	accountName = accountName.replace(/[^a-zA-Z0-9-]+/,"-");
	accountName = accountName.replace(/[-]+/,"-");

	if(accountName.length > 127) {
		var digest = new GlideDigest();
		accountName = digest.getMD5Base64(accountName);
	}

	return accountName;
}

init();

