init();

var IncAssignmentGroup = "ITSM SWE - Interop"; // ITSM SWE - Interop
var RitmStatus = {
	closed_complete: 3,
	closed_incomplete: 4
};

var helpers = new x_micr5_scriptexe.ScriptExecutionHelpers();
var data = workflow.scratchpad.scriptData;
var isSuccess=true;
var workNoteUpdate = "Script updated successfully.";
if(data.update_creds==true) {
	isSuccess=false;
	var Encrypter = new GlideEncrypter();

	var keyVaultName = helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.KeyVaultName');
	var clientID = helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.ScriptExecutionAppId');
	var clientSecret = helpers.getInstanceTargetedSysProperty('x_micr5_scriptexe.ScriptExecutionAppSecret');
	var loginURL = gs.getProperty('microsoft.cas.login.endpoint');



	var endpoint = "https://"+keyVaultName+".vault.azure.net/secrets/"+ data.keyvault_credential_name + "?api-version=7.0";
	var resource = "https://vault.azure.net";
	var secretData = {
		attributes: null,
		contentType: data.cred_username,
		tags: null,
		value: Encrypter.decrypt(data.cred_password)
	};

	var casService = new global.PostToCAS();
	var returnObj = casService.postToCASWithToken(loginURL, resource, clientID, clientSecret, endpoint, secretData, "PUT", "success", "error");
	returnObj = global.JSON.parse(returnObj);
	if(returnObj.status_code==200) {
		isSuccess = true;
		workNoteUpdate = "Updated account password for " + data.cred_username;
	} else {
		workNoteUpdate = "An error occurred updating the password for " + data.cred_username + ". Assigning to the ITSM SWE - Interop team for triage";
	}
}

workflow.info("num: " + current.number);

var parentRecord = new GlideRecord('sc_request');
parentRecord.get('sys_id',current.request);
current.work_notes = workNoteUpdate;

if(isSuccess) {
	// auto-close request
	parentRecord.request_state = 'closed_complete';	
	current.state = RitmStatus.closed_complete;
	current.work_notes = "Successfully completed script update. Automatically closing this request.";
	parentRecord.update();		
} else {
	// problem? Create INC?				
	var inc = new GlideRecord("incident");
	inc.initialize();
	inc.setDisplayValue('assignment_group', IncAssignmentGroup);	
	inc.setDisplayValue('cmdb_ci',"ITSM - ServiceNow");
	inc.caller_id = parentRecord.requested_for.toString();
	inc.category = "applicationandservice";
	inc.subcategory = "configuration";
	inc.short_description = "Could not complete script update for " + current.number;
	inc.description = "Script Execution: Update Script Permissions failed to update correctly. Please investigate";
	inc.work_notes = "Error occurred on RITM: [code]<a href='"+ current.getLink() + "'>"+current.number+"</a>[/code]";
	inc.insert();
	
	current.work_notes = "Failed to complete script update. The incident [code]<a href='"+inc.getLink()+"'>" + inc.number + "</a>[/code] has been created to track the issue";
}

